import React, { Component } from 'react';

import { BrowserRouter as Router, Route, Link} from 'react-router-dom';
import { Table, Button, ButtonGroup, ButtonToolbar} from 'reactstrap';
import {Card, CardImg, CardText, CardBody, CardTitle, CardDeck, CardGroup } from 'reactstrap';

import './index.js';
import './css/DashBoard.css';

export class DashBoard extends Component {

    constructor(props) {
        super(props)
        this.state = {
            metricAreaInfo : this.props.metricAreaInfo,
            metricAreaID : this.props.metricAreaID,
            metricAreaOwner: this.props.metricAreaOwner,
            mACM: this.props.metricAreaCalculationsMonths,
            mACQ: this.props.metricAreaCalculationsQuarters,
            maCY: this.props.metricAreaCalculationYears
        }
    }

    render() {
        console.log(this.state.metricAreaInfo)

        return(        
            <div className = "body">
            <h1> {this.state.metricAreaInfo} </h1>
            <h1> {this.state.metricAreaID} </h1>
            <h2> Metric Area Summary </h2>
            <h3> Owner: {this.state.metricAreaOwner} </h3>

            <Table bordered align="center">
                <thead>
                    <tr>
                    <th> Metric </th>
                    <th> Metric Calculations </th>

                    </tr>
                </thead>


                {/* Table representing metric and metric caluclation */}
                <tbody>
                    <MetricCalculationRow></MetricCalculationRow> 
                </tbody>
            </Table>


            {/* Monthly Information */}
            {
            <MetricMonthly>

            </MetricMonthly>
            }

            {/* Quarterly Information */}
            {
            <MetricQuarterly>

            </MetricQuarterly>
            }
            {/* Yearly Information */}
            {
            <MetricQuarterly>

            </MetricQuarterly>
            }
        </div>
        )
    }
}

class MetricCalculationRow extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (

            <tr>
                <th>
                    {this.props.metric}
                </th>
                <th>
                    {this.props.metricCalc}
                </th>
            </tr>
        )
    }
}

class MetricMonthly extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div>
                <h2>{this.props.quarter}</h2>
                <Table responsive>
                    <tbody>
                        <tr>
                            <th>Actual</th>
                            <th>Target</th>
                            <th>Highlights</th>
                            <th>Lowlights</th>
                            <th>Correction of Error</th>
                        </tr>
                        <tr>
                            <th>{this.props.actual}</th>
                            <th>{this.props.target}</th>
                            <th>{this.props.highlights}</th>
                            <th>{this.props.lowlights}</th>
                            <th>{this.props.correction}</th>
                        </tr>
                    </tbody>
                </Table>
            </div>
        )
    }
}

class MetricQuarterly extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div>
                <Table responsive>
                    <tbody>
                        <tr>
                            <th>Actual</th>
                            <th>Target</th>
                            <th>Highlights</th>
                            <th>Lowlights</th>
                            <th>Correction of Error</th>
                        </tr>
                        <tr>
                            <th>{this.props.actual}</th>
                            <th>{this.props.target}</th>
                            <th>{this.props.highlight}</th>
                            <th>{this.props.lowlight}</th>
                            <th>{this.props.correction}</th>
                        </tr>
                    </tbody>
                </Table>
            </div>
        )
    }
}

class MetricAnnuals extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div>
                <Table>
                    <tbody>
                        <tr>
                            <th>Actual</th>
                            <th>Target</th>
                            <th>Highlights</th>
                            <th>Lowlights</th>
                            <th>Correction of Error</th>
                        </tr>
                        <tr>
                            <th>{this.props.actual}</th>
                            <th>{this.props.target}</th>
                            <th>{this.props.highlight}</th>
                            <th>{this.props.lowlight}</th>
                            <th>{this.props.correction}</th>
                        </tr>
                    </tbody>
                </Table>
            </div>
        )
    }
}
