class MetricMonthly extends Component {
    constructor(props) {
        super(props);
    }

    componentDidMount() {
    }

    month(num) {
        switch(num) {
            case 1:
                return "January"
            case 2:
                return "February"
            case 3: 
                return "March"
            case 4:
                return "April"
            case 5:
                return "May"
            case 6: 
                return "June"
            case 7:
                return "July"
            case 8:
                return "August"
            case 9:
                return "September"
            case 10:
                return "October"
            case 11:
                return "November"
            case 12:
                return "December"
        }
    }

    render() {

        let actualValue = this.props.actual
        let monthValue = this.month(this.props.month)

        // If there is no value existing for the actual yet
        if (!actualValue) {
            actualValue = "N/A"
        }

        return (
            <div>
                <h2>{monthValue}</h2>
                <Table responsive>
                    <tbody>
                        <tr>
                            <th>Actual</th>
                            <th>Target</th>
                        </tr>
                        <tr>
                            <th>{actualValue}</th>
                            <th>{this.props.target}</th>
                        </tr>
                    </tbody>
                </Table>
            </div>
        )
    }
}

